pragma solidity ^0.4.18;

import 'zeppelin-solidity/contracts/token/ERC20/ERC20.sol';

import "./ADPChannelManager.sol";

contract ADPChannel {

  enum SettlementType{Sender, Receiver}
  enum ContractStatus{Ready, WaitingForSettlement}

  event Withdrawal(address to, uint totalPaidToReceiver);

  uint GracePeriod = 1 days;
  uint TimeSecurityFix = 1 minutes;
  uint SenderSettlementTime;
  uint LastSettledNonce;
  uint LastSettledCumulativeAmount;

  address Token;
  address Sender;
  address Receiver;
  address ChannelOwner;
  address ADPChannelManagerAddress;

  uint SenderWithdrawableAmount;
  uint ReceiverWithdrawableAmount;
  uint CumulativeAmountWithdrawedByReceiver;
  
  constructor(address token, address sender, address receiver, address adpChannelManager, uint gracePeriod) public {
    Token = token;
    Sender = sender;
    Receiver = receiver; 
    ChannelOwner = msg.sender;
    GracePeriod = gracePeriod;
    ADPChannelManagerAddress = adpChannelManager;
    if(GracePeriod == 0){
      GracePeriod = 1 days;
    }
  }

  function settlementReceiver(uint nonce, uint totalPaidToReceiver, 
    uint8 signReceiverV, bytes32 signReceiverR, bytes32 signReceiverS,
    uint8 signSenderV, bytes32 signSenderR, bytes32 signSenderS) public {

    require (msg.sender == ChannelOwner || msg.sender == Receiver);
    require (nonce >= LastSettledNonce);

    if(nonce != LastSettledNonce) {
      bytes32 hash = keccak256(abi.encodePacked(nonce, totalPaidToReceiver));
      require (verify(hash, signReceiverV, signReceiverR, signReceiverS, Receiver));
      require (verify(hash, signSenderV, signSenderR, signSenderS, Sender));
    }
    else {
      totalPaidToReceiver = LastSettledCumulativeAmount; // receiver lost last ccs, sends newly constructed without signatures
    }
    
    uint prevReceiverWithdrawablewAmount = ReceiverWithdrawableAmount;
    ReceiverWithdrawableAmount = totalPaidToReceiver - CumulativeAmountWithdrawedByReceiver;
    uint withdrawalAmount = ReceiverWithdrawableAmount - prevReceiverWithdrawablewAmount;
    

    //check sender settlement totalPaidToReceiver
    if(SenderSettlementTime > now) {
      ERC20 tokenObject = ERC20(Token);
      if(tokenObject.balanceOf(this) < ReceiverWithdrawableAmount + SenderWithdrawableAmount) {
        SenderWithdrawableAmount = 0;
      }
    }

    SenderSettlementTime = 0;
    LastSettledNonce = nonce;
    LastSettledCumulativeAmount = totalPaidToReceiver;

    ADPChannelManager(ADPChannelManagerAddress).publishSettlement(Token, Sender, nonce, withdrawalAmount, SettlementType.Receiver);
  }

  function settlementSender(uint nonce, uint totalPaidToReceiver, uint withdrawalAmount,
    uint8 signReceiverV, bytes32 signReceiverR, bytes32 signReceiverS,
    uint8 signSenderV, bytes32 signSenderR, bytes32 signSenderS) public {


    require (msg.sender == ChannelOwner || msg.sender == Receiver || msg.sender == Sender);
    require (nonce >= LastSettledNonce);
    if(nonce != LastSettledNonce) {
      bytes32 hash = keccak256(abi.encodePacked(nonce, totalPaidToReceiver));
      require (verify(hash, signReceiverV, signReceiverR, signReceiverS, Receiver));
      require (verify(hash, signSenderV, signSenderR, signSenderS, Sender));
    }
    else {
      totalPaidToReceiver = LastSettledCumulativeAmount; // sender lost last ccs, sends newly constructed without signatures
    }
    
    ERC20 tokenObject = ERC20(Token);
    // check that sender can withdraw withdrawalAmount
    require(tokenObject.balanceOf(this) >= (totalPaidToReceiver - CumulativeAmountWithdrawedByReceiver) + SenderWithdrawableAmount + withdrawalAmount);
    
    SenderSettlementTime = now + GracePeriod;
    SenderWithdrawableAmount += withdrawalAmount;
    LastSettledNonce = nonce;      
    LastSettledCumulativeAmount = totalPaidToReceiver;
    ADPChannelManager(ADPChannelManagerAddress).publishSettlement(Token, Sender, nonce, withdrawalAmount, SettlementType.Sender);
  }  
  
  function withdrawal () public {
    require (msg.sender == ChannelOwner || msg.sender == Receiver || msg.sender == Sender);
    uint transferAmount;
    ERC20 tokenObject = ERC20(Token);
    if(ReceiverWithdrawableAmount > 0) {
      transferAmount = ReceiverWithdrawableAmount;

      // Case when receiver didn't dispute settlement during grace period, receiver should still be able to withdraw
      if(transferAmount > tokenObject.balanceOf(this)) {
        transferAmount = tokenObject.balanceOf(this); 
      }
      require(tokenObject.transfer(Receiver, transferAmount));
      emit Withdrawal(Receiver, transferAmount);
      ReceiverWithdrawableAmount -= transferAmount;
      CumulativeAmountWithdrawedByReceiver += transferAmount;
    }

    if(SenderSettlementTime < now - TimeSecurityFix && SenderWithdrawableAmount > 0) {
      transferAmount = SenderWithdrawableAmount;
      
      if(transferAmount > tokenObject.balanceOf(this)) {
        transferAmount = tokenObject.balanceOf(this);
      }
      require(tokenObject.transfer(Sender, transferAmount));
      emit Withdrawal(Sender, transferAmount);
      SenderWithdrawableAmount -= transferAmount;
    }
  }

  function getChannel () external view returns(
    uint balance, //Amount of tokens on the account of a Channel Contract
    uint capacity,
    uint senderWithdrawableAmount,  
    uint receiverWithdrawableAmount,
    uint nonce,
    uint cumulativeAmountWithdrawedByReceiver,
    ContractStatus status
    ) {
    senderWithdrawableAmount = SenderWithdrawableAmount;
    receiverWithdrawableAmount = ReceiverWithdrawableAmount;
    nonce = LastSettledNonce;
    ERC20 tokenObject = ERC20(Token);
    balance = tokenObject.balanceOf(this);
    capacity = balance - LastSettledCumulativeAmount + CumulativeAmountWithdrawedByReceiver - SenderWithdrawableAmount;
    cumulativeAmountWithdrawedByReceiver = CumulativeAmountWithdrawedByReceiver;
    if(SenderSettlementTime < now)
      status = ContractStatus.Ready;
    else
      status = ContractStatus.WaitingForSettlement;
  }

  function x(bytes32 hash, uint8 v, bytes32 r, bytes32 s, address signerAddress) public pure returns(bytes32) {
      return r;
  }

  function verify(bytes32 hash, uint8 v, bytes32 r, bytes32 s, address signerAddress) public pure returns(bool) {
    if(ecrecover(hash, v, r, s) == signerAddress)
      return true;
    bytes memory prefix = "\x19Ethereum Signed Message:\n32";
    bytes32 prefixedHash = keccak256(abi.encodePacked(prefix, hash));
    return ecrecover(prefixedHash, v, r, s) == signerAddress;
  }
}
